package unary;

public class DenyLogicOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// boolean으로된 애만 true/false 가능
		boolean play = true;
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
	}

}
